import gql from 'graphql-tag';

export const mainQuery = gql`{
    Artist(where: {ArtistId: {_eq: $_eq}}) {
      Name
      ArtistId
    }
}`;

export const mainQueryCache = gql`{
  Artist @client {
    Name
    ArtistId
  }
  Album @client {
    Title
    AlbumId
  }
}
`;
