import React from 'react';
import { Button } from '@material-ui/core';

import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Link from 'next/link' ;

import Welcome from "./Welcome"

const myQuery = gql`{
  Artist {
    Name
    ArtistId
  }
  Album {
    Title
    AlbumId
  }
}
`;

function App() {
  const { data } = useQuery(myQuery);
  return (
    <div className="App">
      <header className="App-header">
        <Welcome data={data} />         
        <div>I also have a cool button</div>
        <Button variant="contained" color="primary">Like me!</Button>
        <Link href="/about" style="color:white;">Next Page</Link>
      </header>


    </div>
  );
}

export default App;
