import React from 'react';
import { Button } from '@material-ui/core';

import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Link from 'next/link' ;


import Welcome from "./Welcome"
// import classes from '*.module.css';

const myQuery = gql`{
  Artist {
    Name
    ArtistId
  }
  Album {
    Title
    AlbumId
  }
}
`;

const classes = {

  upscale : {
    color: '#ffffff'
  }

}

function App() {
  const { data } = useQuery(myQuery);

  return (
    <div className="App">
      <header className="App-header">
        <Welcome data={data} />         
        <div>I also have a cool button</div>
        <Button variant="contained" color="primary">Like me!</Button>
        <Link href="/about" class={classes.upscale}><a>Next Page</a></Link>
      </header>


    </div>
  );
}

export default App;
