import gql from 'graphql-tag';

export const MAIN_QUERY = gql`{
    Artist(where: {ArtistId: {_eq: $_eq}}) {
      Name
      ArtistId
    }
}`;


const GET_DOG_PHOTO = gql`
  query Dog($breed: String!) {
    dog(breed: $breed) {
      id
      displayImage
    }
  }
`;

export const mainQueryCache = gql`{
  Artist @client {
    Name
    ArtistId
  }
  Album @client {
    Title
    AlbumId
  }
}
`;
