import gql from 'graphql-tag';

export const mainQuery = gql`{
  query GetThemAll {
    Artist {
      Name
      ArtistId
    }
    Album {
      Title
      AlbumId
    }
  }
}`;

export const mainQueryCache = gql`{
  Artist {
    Name
    ArtistId
  }
  Album {
    Title
    AlbumId
  }
}
`;
