import React, { useContext } from 'react';
import {UserContext } from './UserContext';

import { makeStyles } from '@material-ui/core/styles';

import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';


const useStyles = makeStyles(theme => ({
    rootz: {
      color: '#ffffff'
    }
}));

const Myradio = (() => {

  const classes = useStyles();
  const [store, setStore] = useContext(UserContext);

  const artist = (store == 'first') ? data[0] : (store == 'second') ? data[2] : data[3]; 
  // const [value, setValue] = useState('female');

  // const handleChange = (event) => {
  //   setStore(event.target.value)
  //   console.log(event.target.value)
  // };

  return (
    <FormControl component="fieldset">
      <RadioGroup default="female" aria-label="gender" name="gender1" value={store} onChange={()=>setStore(event.target.value)}>
        <FormControlLabel value="first" control={<Radio />} label="First" />
        <FormControlLabel value="second" control={<Radio />} label="Second" />
        <FormControlLabel value="third" control={<Radio />} label="Third" />
      </RadioGroup>
      <h2>{artist}</h2>
    </FormControl>

  );
})

export default Myradio;
